mapper.select:
SELECT COUNT(*) FROM GOOD
WHERE REVIEW_NUM = #{review_num}
AND ID = #{id}

mapper.delete:
DELETE GOOD
WHERE REVIEW_NUM = #{review_num}
AND ID = #{id}

mapper.update:
UPDATE REVIEW SET
GOOD = GOOD+1
WHERE REVIEW_NUM = #{review_num}

UPDATE REVIEW SET
GOOD = GOOD-1
WHERE REVIEW_NUM = #{review_num}
